rss.onRefresh:
    
    /* SET THE REFRESH RATE  */

    setRefreshTime(5000);
    redrawDisplay();

mediaDisplay.script:
intIndex = getFocusItemIndex();
strChannelStreamURL = getStringArrayAt(urlArray , intIndex);
strChannelName = getStringArrayAt(titleArray , intIndex);

mediaDisplay.onUserInput:
        input = currentUserInput();
        /* CHECK TO SEE IF THE USER HIT THE ENTER BUTTON */
	if (input == "enter") {              
            	/* STOP THE STREAM IF PLAYING */

            	PlayItemURL("", 1);
    
            	/* PLAY THE REQUESTED STREAM */
		showIdle();
            	PlayItemURL(strChannelStreamURL, 0,"mediaDisplay", "previewWindow");
		cancelIdle();
        	strNowPlaying = "Acum ascultaţi: " + strChannelName;
            	true;
			
        }
	if (input == "stop" || input == "edit" || input == "setup" || input == "guide") {			               
            	/* STOP THE STREAM IF PLAYING */
            	PlayItemURL("", 1);   
            	strNowPlaying = "";	
            	true;
        }

title.script:
                /* GET THE SELECTED INDEX */

                intIndex = getQueryItemIndex();

		getStringArrayAt(titleArray , intIndex);

rss.onExit:
	setRefreshTime(-1);

    /* STOP ANY STREAM THAT IS CURRENTLY PLAYING */

    PlayItemURL("",1);
