rss.onEnter:
print("enter seek popup.rss");
bReturn = 1;
vidProgress = getEnv("videoStatus");
elapsed = getStringArrayAt(vidProgress, 0);
total = getStringArrayAt(vidProgress, 1);
/* if total time is longer than 30 seconds */
if (total < 30)
{
	step_left = 1;
	step_right = 1;
}
else
{
	step_left = 10;
	step_right = 30;
}
time2 = total;
redrawDisplay();

mediaDisplay.onUserInput:
ret = "false";
input = currentUserInput();
print("1user input is : ", input);
if (input == "right")
{
	elapsed = Add(elapsed, step_right);
	if (elapsed > total)
	{
		elapsed = total;
	}
	redrawDisplay("widget");
	ret = "true";
}
else if (input == "left")
{
	elapsed = Minus(elapsed, step_left);
	if (elapsed < 0)
	{
		elapsed = 0;
	}
	redrawDisplay("widget");
	ret = "true";
}
else if (input == "enter")
{
	bReturn = 0;
	postMessage("return");
	/* avoid transition effect */
	redrawDisplay("no");
	ret = "true";
}
if (input == "return")
{
	print("LOUIS - bReturn : ",bReturn);
	if (bReturn == 1)
	{
		elapsed = -1;
	}
	/* avoid transition effect */
	redrawDisplay("no");
	ret = "false";
}
else if (input == "video_completed" || input == "video_stop")
{
	bReturn = 0;
	playItemURL(-1, 1);
	postMessage("return");
	postMessage("return");
}
ret;
